import React,{useState} from 'react'
import './ExpenseForm.css'

const ExpenseForm =()=>{
    const [enteredTitle,setEnteredTitle] = useState('')
    const changeTitleHandler=(event)=>{
        setEnteredTitle(event.target.value)
        console.log(event.target.value)
    }
    const [enteredAmount,setEnteredAmount] = useState('')
    const changeAmountHandler=(event)=>{
        setEnteredAmount(event.target.value)
        console.log(event.target.value)
    }
    const [enteredDate,setEnteredDate] = useState('')
    const ChangeDateHandler=(event)=>{
        setEnteredDate(event.target.value)
        console.log(event.target.value)
    }   

    return(
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>title</label>
                    <input type="text" onChange={changeTitleHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={changeAmountHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>date</label>
                    <input type="date" min="2019-01-01" max="2022-12-31" onChange={ChangeDateHandler}></input>
                </div>

            </div>
            <div className="new-expense__actions">
                    <button type="submit">new expense</button>
                </div>
        </form>
    )
}

export default ExpenseForm
