import React ,{useState}from 'react'
import './ExpenseFilter.css'


const ExpenseFilter=(props)=>{
    const [firstOption,setFirstOption]= useState('')
    const firstOptionHandler=(event)=>{
        setFirstOption(event.target.value)
        console.log(event.target.value)
    }
    const [secondOption,setSecondOption]=useState('')
    const secondOptionHandler=(event)=>{
        setSecondOption(event.target.value)
        console.log(event.target.value)
    }
    const [thirdOption,setThirdOption]=useState('')
    const thirdOptionHandler=(event)=>{
        setThirdOption(event.target.value)
        console.log(event.target.value)
    }
    const [fourthOption,setFourthOption]=useState('')
    const fourthOptionHandler=(event)=>{
        setFourthOption(event.target.value)
        console.log(event.target.value)
    }

    return(

        <div className="expense-filter">
            <div className="expenses-filter__control">
                <label>filter by year</label>
                <select>
                    <option value='2022' onSelect={firstOptionHandler}>2022</option>
                    <option value='2021' onClick={secondOptionHandler}>2021</option>
                    <option value='2020' onClick={thirdOptionHandler}>2020</option>
                    <option value='2019' onClick={fourthOptionHandler}>2019</option>
                </select>
            </div>
        </div>

    )
}

export default ExpenseFilter
